#include "gprt.h"

// A simple struct representing buffer of ray data as generated by our "mock application"
struct Rays {
    double3* origins;
    double3* directions;
};

struct GenerateRandomRayParams {
    Rays rays; // pointer to ray data buffer
    uint numRays; // number of rays to be generated
    double3 origin; // single origin provided for benchmark case
    uint seed; // seed for random direction generation
};